package com.kn.Strings;

public class MethodsOfString1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1="Hrushi Keswara Reddy";
		System.out.println("Lower case: "+s1.toLowerCase());
		System.out.println("Upper Case: "+s1.toUpperCase());
		System.out.println("Character at given index: "+s1.charAt(0));
		System.out.println("Index of given character: "+s1.indexOf('K'));
		System.out.println("Last index of given Character: "+s1.lastIndexOf('a'));
		System.out.println("Sub string from given Range : "+s1.substring(5));
		
	}

}
